import React from 'react';
import "./JogoVelha.css";

class JogoVelha extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      marker: 'O',
    }
  }

  markGrid(event) {
    const { target } = event;
    const { marker } = this.state;
    if (marker === 'O') {
      this.setState({
        marker: 'X',
      })
      target.innerHTML = 'X';
    }
    else {
      target.innerHTML = 'O';
    }
  }

  gridJogoDaVelha() {
    const arrayGrids = []
    for (let index = 0; index < 9; index++) {
      arrayGrids[index] = <button type="button" key={index} className="grid" onClick={this.markGrid} />
    }
    return arrayGrids;
  }

  render() {
    return (
      <div>
        <div>Jogo da Velha: </div>
        <div className="grid_container">
          {this.gridJogoDaVelha()}
        </div>
      </div>
    );
  }
}

export default JogoVelha;
